var link=document.querySelector(".button-search"),popup=document.querySelector(".search-hotel-form"),arrival=popup.querySelector("[name=arrival-date]"),departure=popup.querySelector("[name=departure-date]"),adults=popup.querySelector("[name=number-of-adult]"),children=popup.querySelector("[name=number-of-children]");link.addEventListener("click",function(e){e.preventDefault(),popup.classList.toggle("form-show")}),popup.addEventListener("submit",function(e){arrival.value&&departure.value&&adults.value&&children.value||(e.preventDefault(),popup.classList.remove("form-error"),popup.offsetWidth=popup.offsetWidth,popup.classList.add("form-error"))}),window.addEventListener("keydown",function(e){27===e.keyCode&&(e.preventDefault(),popup.classList.contains("form-show")&&(popup.classList.remove("form-show"),popup.classList.remove("form-error")))});
